import React, { useState } from "react";
import axios from "axios"; // You need to install axios: npm install axios
import "./Login.css";

const Login = () => {
  // State to store username, password, and error message
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");

  // Handle input change for username and password
  const handleChange = (e) => {
    const { name, value } = e.target;
    if (name === "username") setUsername(value);
    if (name === "password") setPassword(value);
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Send login data to the backend API
      const response = await axios.post("http://your-backend-api-url/api/login", {
        username,
        password,
      });

      // Check if login is successful (this depends on your backend response)
      if (response.data.success) {
        // Redirect or perform any action after successful login
        alert("Login Successful!");
        window.location.href = "/dashboard"; // Redirect to dashboard or homepage
      } else {
        // Handle failed login attempt
        setErrorMessage("Invalid username or password.");
      }
    } catch (error) {
      setErrorMessage("An error occurred. Please try again.");
    }
  };

  return (
    <div className="login-box">
      <h2>Login</h2>
      <form onSubmit={handleSubmit}>
        <div className="user-box">
          <input
            type="text"
            name="username"
            value={username}
            onChange={handleChange}
            required
          />
          <label>Username</label>
        </div>
        <div className="user-box">
          <input
            type="password"
            name="password"
            value={password}
            onChange={handleChange}
            required
          />
          <label>Password</label>
        </div>
        <a href="#">
          Submit
          <span></span>
        </a>
      </form>
      {errorMessage && <p className="error-message">{errorMessage}</p>}
    </div>
  );
};

export default Login;
