import React, { useEffect } from "react";
import "./Homepage.css"; // Ensure the CSS file is correctly imported

const Homepage = () => {
  useEffect(() => {
    const snowContainer = document.querySelector(".mythomagic");
    if (snowContainer) {
      for (let i = 0; i < 9; i++) {
        const snowflake = document.createElement("span");
        snowContainer.appendChild(snowflake);
      }
    }
  }, []);

  return (
    <div>
      <h1>MythoMagic</h1>
      <div className="mythomagic"></div>
      <a href="https://x.com/nodws" id="btn-twtr" target="_blank" rel="noopener noreferrer">
        Follow me @nodws
      </a>
    </div>
  );
};

export default Homepage;
