import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom"; // Import Link from react-router-dom
import "./Homepage.css"; // Ensure the CSS file is correctly imported

const Homepage = () => {
  const [dynamicText, setDynamicText] = useState("MythoMagic"); // Keeping text dynamic for the homepage title

  useEffect(() => {
    const interval = setInterval(() => {
      // Changing the opacity to create the fade-in/fade-out effect
      setDynamicText((prevText) => (prevText === "" ? "MythoMagic" : ""));
    }, 2000); // Toggle text every 2 seconds

    return () => clearInterval(interval); // Cleanup interval on component unmount
  }, []);

  useEffect(() => {
    const snowContainer = document.querySelector(".mythomagic");
    if (snowContainer) {
      for (let i = 0; i < 9; i++) {
        const snowflake = document.createElement("span");
        snowContainer.appendChild(snowflake);
      }
    }
  }, []);

  return (
    <div className="homepage-container">
      <h1>{dynamicText}</h1>
      <div className="mythomagic"></div>

      <div className="auth-buttons">
        {/* Link for Login and Sign Up */}
        <Link to="/login" className="auth-button">Login</Link>
        <Link to="/signup" className="auth-button">Sign Up</Link>
      </div>
    </div>
  );
};

export default Homepage;
