import React, { useState } from 'react';
import './authform.css';

const AuthForm = () => {
  const [isLogin, setIsLogin] = useState(true); // Toggle between Login and Signup
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });

  // Handle input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prevData) => ({ ...prevData, [name]: value }));
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    const url = isLogin ? '/api/login' : '/api/signup';
    const method = isLogin ? 'POST' : 'POST';
    const body = JSON.stringify(formData);

    const response = await fetch(url, {
      method,
      headers: { 'Content-Type': 'application/json' },
      body,
    });

    const data = await response.json();

    if (response.ok) {
      alert(isLogin ? 'Login successful!' : 'Signup successful!');
      // Handle successful login/signup (e.g., redirect to another page or set user state)
    } else {
      alert(data.message || 'An error occurred');
    }
  };

  return (
    <div className="auth-container">
      <h2>{isLogin ? 'Sign In' : 'Sign Up'}</h2>
      <form onSubmit={handleSubmit} className="auth-form">
        {!isLogin && (
          <input
            type="text"
            name="username"
            value={formData.username}
            onChange={handleInputChange}
            placeholder="Username"
            required
          />
        )}
        <input
          type="email"
          name="email"
          value={formData.email}
          onChange={handleInputChange}
          placeholder="Email"
          required
        />
        <input
          type="password"
          name="password"
          value={formData.password}
          onChange={handleInputChange}
          placeholder="Password"
          required
        />
        <button type="submit">{isLogin ? 'Sign In' : 'Sign Up'}</button>
      </form>
      <p>
        {isLogin ? (
          <span>
            Don't have an account?{' '}
            <button onClick={() => setIsLogin(false)}>Sign up here</button>
          </span>
        ) : (
          <span>
            Already have an account?{' '}
            <button onClick={() => setIsLogin(true)}>Sign in here</button>
          </span>
        )}
      </p>
    </div>
  );
};

export default AuthForm;
